KAMANJA_HOME={InstallDirectory}

java -jar $KAMANJA_HOME/bin/KVInit-1.0 --kvname System.AccountAggregatedUsage          --config $KAMANJA_HOME/config/Engine1Config.properties --csvpath $KAMANJA_HOME/input/SampleApplications/data/AccountAggregatedUsage_Telecom.dat           --keyfieldname actNo
java -jar $KAMANJA_HOME/bin/KVInit-1.0 --kvname System.AccountInfo                     --config $KAMANJA_HOME/config/Engine1Config.properties --csvpath $KAMANJA_HOME/input/SampleApplications/data/AccountInfo_Telecom.dat                      --keyfieldname actNo
java -jar $KAMANJA_HOME/bin/KVInit-1.0 --kvname System.SubscriberAggregatedUsage       --config $KAMANJA_HOME/config/Engine1Config.properties --csvpath $KAMANJA_HOME/input/SampleApplications/data/SubscriberAggregatedUsage_Telecom.dat        --keyfieldname msisdn
java -jar $KAMANJA_HOME/bin/KVInit-1.0 --kvname System.SubscriberGlobalPreferences     --config $KAMANJA_HOME/config/Engine1Config.properties --csvpath $KAMANJA_HOME/input/SampleApplications/data/SubscriberGlobalPreferences_Telecom.dat      --keyfieldname PrefType
java -jar $KAMANJA_HOME/bin/KVInit-1.0 --kvname System.SubscriberInfo                  --config $KAMANJA_HOME/config/Engine1Config.properties --csvpath $KAMANJA_HOME/input/SampleApplications/data/SubscriberInfo_Telecom.dat                   --keyfieldname msisdn
java -jar $KAMANJA_HOME/bin/KVInit-1.0 --kvname System.SubscriberPlans                 --config $KAMANJA_HOME/config/Engine1Config.properties --csvpath $KAMANJA_HOME/input/SampleApplications/data/SubscriberPlans_Telecom.dat                  --keyfieldname planName